      void deleteItem(Item item) {    realm.write(() => realm.delete(item));    notifyListeners();  }
      Future<void> updateItem(Item item,      {String? summary,      bool? isComplete,      int? priority}) async {    realm.write(() {      if (summary != null) {        item.summary = summary;      }      if (isComplete != null) {        item.isComplete = isComplete;      }      if (priority != null) {        item.priority = priority;      }    });    notifyListeners();  }
