import org.bson.Document;import org.bson.conversions.Bson;
import com.mongodb.client.ChangeStreamIterable;import com.mongodb.client.MongoClient;import com.mongodb.client.MongoClients;import com.mongodb.client.MongoCollection;import com.mongodb.client.MongoDatabase;import com.mongodb.client.model.Filters;import com.mongodb.client.model.changestream.FullDocument;
public class Watch {    public static void main( String[] args ) {
        // Replace the uri string with your MongoDB deployment's connection string        String uri = "<connection string uri>";
        try (MongoClient mongoClient = MongoClients.create(uri)) {
            MongoDatabase database = mongoClient.getDatabase("sample_mflix");            MongoCollection<Document> collection = database.getCollection("movies");
            List<Bson> pipeline = Arrays.asList(                Aggregates.match(                        Filters.in("operationType",                                Arrays.asList("insert", "update"))));            ChangeStreamIterable<Document> changeStream = database.watch(pipeline)                .fullDocument(FullDocument.UPDATE_LOOKUP);            // variables referenced in a lambda must be final; final array gives us a mutable integer            final int[] numberOfEvents = {0};            changeStream.forEach(event -> {            System.out.println("Received a change to the collection: " + event);                if (++numberOfEvents[0] >= 2) {                  System.exit(0);                }            });        }    }}  WatchCompanion:

